import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import { ThemeProvider, useTheme } from './utils/theme';
import HeroSlider from './components/HeroSlider';
import MainContent from './components/MainContent';
import CategorySection from './components/CategorySection';
import HowItWorks from './components/HowItWorks';
import BusinessCTA from './components/BusinessCTA';
import CategoryLightbox from './components/CategoryLightbox';
import Footer from './components/Footer';
import Newsletter from './components/Newsletter';
import { FaPhone, FaMapMarkerAlt, FaHeart, FaShareAlt, FaGlobe } from 'react-icons/fa';

function AppContent() {
  const { theme } = useTheme();
  const [isCategoryLightboxOpen, setIsCategoryLightboxOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const handleCategoryClick = (categoryKey) => {
    setSelectedCategory(categoryKey);
    setIsCategoryLightboxOpen(true);
  };

  const handleCloseCategoryLightbox = () => {
    setIsCategoryLightboxOpen(false);
    setSelectedCategory(null);
  };

  // Componente genérico para cualquier categoría (excepto cartelera)
  function LightboxCategoryContent({ categoria, titulo }) {
    const ciudades = [
      { key: 'monterrey', label: 'Monterrey' },
      { key: 'guadalajara', label: 'Guadalajara' },
      { key: 'cdmx', label: 'CDMX' },
      { key: 'cancun', label: 'Cancún' },
      { key: 'palenque', label: 'Palenque' },
      { key: 'puertoVallarta', label: 'Puerto Vallarta' },
      { key: 'veracruz', label: 'Veracruz' },
    ];
    const [ciudad, setCiudad] = useState(ciudades[0].key);
    const [datos, setDatos] = useState([]);
    const [descripcionCiudad, setDescripcionCiudad] = useState("");
    const [imagenCiudad, setImagenCiudad] = useState("");
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
      setLoading(true);
      setError(null);
      fetch(`/data/${categoria}/${ciudad}.json`)
        .then(res => {
          if (!res.ok) throw new Error('No se pudo cargar el archivo de la ciudad');
          return res.json();
        })
        .then(data => {
          if (data && data.lugares) {
            setDatos(Array.isArray(data.lugares) ? data.lugares.slice(0, 18) : []);
            setDescripcionCiudad(data.descripcion || "");
            setImagenCiudad(data.imagen || "");
          } else if (Array.isArray(data)) {
            setDatos(data.slice(0, 18));
            setDescripcionCiudad("");
            setImagenCiudad("");
          } else {
            setDatos([]);
            setDescripcionCiudad("");
            setImagenCiudad("");
          }
          setLoading(false);
        })
        .catch(err => {
          setError('Error al cargar los datos de la ciudad');
          setLoading(false);
        });
    }, [ciudad, categoria]);

    return (
      <div className="category-lightbox-container">
        <div className="category-lightbox-header">
          <h2>{titulo}</h2>
          {imagenCiudad && (
            <img src={imagenCiudad} alt={ciudad} style={{ width: '100%', maxHeight: '180px', objectFit: 'cover', borderRadius: '12px', marginBottom: '10px' }} />
          )}
          {descripcionCiudad && (
            <p style={{ fontSize: '1.05em', color: '#333', marginBottom: '10px' }}>{descripcionCiudad}</p>
          )}
        </div>
        <div className="category-lightbox-tabs destinos-tabs">
          {ciudades.map((c, idx) => (
            <button
              key={c.key}
              className={ciudad === c.key ? 'active' : ''}
              style={ciudad === c.key ? { borderBottom: '3px solid #FF004F', color: '#FF004F' } : {}}
              onClick={() => setCiudad(c.key)}
            >
              {c.label}
            </button>
          ))}
        </div>
        <div className="category-lightbox-content">
          {loading && <p>Cargando...</p>}
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <div className="business-grid">
            {datos.map((item, idx) => (
              <div className="business-card" key={item.id || idx} style={{
                background: 'rgba(255,255,255,0.15)',
                backdropFilter: 'blur(10px)',
                borderRadius: '12px',
                boxShadow: '0 4px 16px rgba(0,0,0,0.12)',
                border: '1px solid rgba(255,255,255,0.2)',
                overflow: 'hidden',
                display: 'flex',
                flexDirection: 'column',
                minHeight: '350px',
              }}>
                <img src={item.imagen || item.image} alt={item.nombre || item.name} className="business-card-image" style={{ width: '100%', height: '180px', objectFit: 'cover', borderRadius: '12px 12px 0 0' }} />
                <div className="business-card-content" style={{ flex: 1, padding: '16px' }}>
                  <h4>{item.nombre || item.name}</h4>
                  <span className="badge" style={{ background: '#FF004F', color: '#fff', borderRadius: '8px', padding: '2px 10px', fontSize: '0.8em', marginBottom: '8px', display: 'inline-block' }}>{item.estado || item.status || 'Abierto'}</span>
                  <p style={{ margin: '8px 0', fontSize: '0.95em' }}>{item.descripcion || item.description}</p>
                  <div className="card-meta" style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '8px' }}>
                    <span className="rating" style={{ color: '#FFD700', fontWeight: 'bold' }}>{'★'.repeat(item.calificacion || item.rating || 0)}</span>
                    {item.precio && <span className="price" style={{ color: '#333', fontWeight: 'bold' }}>{item.precio}</span>}
                  </div>
                  <div className="card-buttons" style={{ display: 'flex', gap: '10px', marginTop: 'auto' }}>
                    {item.telefono && <a href={`tel:${item.telefono}`} title="Llamar" className="icon-btn"><FaPhone /></a>}
                    {item.direccion && <a href={item.maps || '#'} target="_blank" rel="noopener noreferrer" title="Ver en mapa" className="icon-btn"><FaMapMarkerAlt /></a>}
                    <button title="Favorito" className="icon-btn"><FaHeart /></button>
                    <button title="Compartir" className="icon-btn"><FaShareAlt /></button>
                    {item.web && item.web !== 'NA' && <a href={item.web} target="_blank" rel="noopener noreferrer" title="Sitio web" className="icon-btn"><FaGlobe /></a>}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="category-lightbox-footer" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: '10px', marginTop: '24px', flexWrap: 'wrap' }}>
          <button className="footer-btn" style={{ flex: 1, minWidth: '120px' }}>Explorar más</button>
          <button className="footer-btn main-btn" style={{ flex: 2, minWidth: '180px', fontSize: '1.1em', background: '#FF004F', color: '#fff', borderRadius: '8px', padding: '12px 0' }}>Ver Directorio Completo</button>
          <button className="footer-btn" style={{ flex: 1, minWidth: '120px' }}>Otros destinos</button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <Header />
      <HeroSlider />
      <CategorySection onCategoryClick={handleCategoryClick} />
      <HowItWorks />
      <BusinessCTA />
      <MainContent />
      <Newsletter />
      <Footer />
      <CategoryLightbox
        isOpen={isCategoryLightboxOpen}
        onClose={handleCloseCategoryLightbox}
        title={selectedCategory ? selectedCategory : ''}
      >
        {selectedCategory === 'destinos' ? (
          <LightboxCategoryContent categoria="destinos" titulo="Destinos" />
        ) : selectedCategory === 'gastronomia' ? (
          <LightboxCategoryContent categoria="gastronomia" titulo="Gastronomía" />
        ) : selectedCategory === 'hospedaje' ? (
          <LightboxCategoryContent categoria="hospedaje" titulo="Hospedaje" />
        ) : selectedCategory === 'directorio' ? (
          <LightboxCategoryContent categoria="directorio" titulo="Directorio" />
        ) : selectedCategory === 'promociones' ? (
          <LightboxCategoryContent categoria="promociones" titulo="Promociones" />
        ) : selectedCategory === 'eventos' ? (
          <LightboxCategoryContent categoria="eventos" titulo="Eventos" />
        ) : selectedCategory === 'servicios' ? (
          <LightboxCategoryContent categoria="servicios" titulo="Servicios" />
        ) : selectedCategory && (
          <div>
            <p>Contenido de la categoría: <strong>{selectedCategory}</strong></p>
            {/* Aquí puedes renderizar información dinámica de la categoría seleccionada */}
          </div>
        )}
      </CategoryLightbox>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
