import React, { useState } from 'react';
import monterreyCartelera from '../data/cartelera/monterrey.json';

const CarteleraTabs = () => {
  const [selectedTab, setSelectedTab] = useState('monterrey');
  // Solo un tab por ahora, pero preparado para más ciudades si se agregan
  const tabs = [
    { key: 'monterrey', label: 'Monterrey' }
  ];

  return (
    <div className="cartelera-tabs">
      <div className="tabs">
        {tabs.map(tab => (
          <button
            key={tab.key}
            className={selectedTab === tab.key ? 'active' : ''}
            onClick={() => setSelectedTab(tab.key)}
          >
            {tab.label}
          </button>
        ))}
      </div>
      <div className="cards">
        {monterreyCartelera.map(card => (
          <div key={card.id} className="cartelera-card">
            <img src={card.image} alt={card.title} />
            <h3>{card.title}</h3>
            <p>{card.description}</p>
            <span>{card.date}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarteleraTabs;
