import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import { ThemeProvider, useTheme } from './utils/theme';
import HeroSlider from './components/HeroSlider';
import MainContent from './components/MainContent';
import CategorySection from './components/CategorySection';
import HowItWorks from './components/HowItWorks';
import BusinessCTA from './components/BusinessCTA';
import CategoryLightbox from './components/CategoryLightbox';
import GastronomiaLightbox from './components/GastronomiaLightbox';
import Footer from './components/Footer';
import Newsletter from './components/Newsletter';

function AppContent() {
  const { theme } = useTheme();
  const [isCategoryLightboxOpen, setIsCategoryLightboxOpen] = useState(false);
  const [isGastronomiaLightboxOpen, setIsGastronomiaLightboxOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const handleCategoryClick = (categoryKey) => {
    setSelectedCategory(categoryKey);
    
    // Si es gastronomía, abrir el lightbox específico
    if (categoryKey === 'gastronomia') {
      setIsGastronomiaLightboxOpen(true);
    } else {
      setIsCategoryLightboxOpen(true);
    }
  };

  const handleCloseCategoryLightbox = () => {
    setIsCategoryLightboxOpen(false);
    setSelectedCategory(null);
  };

  const handleCloseGastronomiaLightbox = () => {
    setIsGastronomiaLightboxOpen(false);
    setSelectedCategory(null);
  };

  return (
    <div className="App">
      <Header />
      <HeroSlider />
      <CategorySection onCategoryClick={handleCategoryClick} />
      <HowItWorks />
      <BusinessCTA />
      <MainContent />
      <Newsletter />
      <Footer />
      <CategoryLightbox
        isOpen={isCategoryLightboxOpen}
        onClose={handleCloseCategoryLightbox}
        category={selectedCategory}
      />
      <GastronomiaLightbox
        isOpen={isGastronomiaLightboxOpen}
        onClose={handleCloseGastronomiaLightbox}
        selectedCategory={selectedCategory}
        selectedSubcategory="monterrey"
      />
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
