import React, { useState } from 'react';
import { FaTimes, FaMapMarkerAlt, FaShareAlt, FaHeart, FaPlus, FaWhatsapp, FaPhone, FaCalendarAlt, FaGlobe } from 'react-icons/fa';
import './../styles/CategoryLightbox.css';

// Datos genéricos para otras categorías (placeholder)
const genericBusinessData = {
  monterrey: [{ id: 1, name: 'Contenido Genérico MTY', desc: 'Descripción...' }],
  guadalajara: [{ id: 2, name: 'Contenido Genérico GDL', desc: 'Descripción...' }],
  cdmx: [{ id: 3, name: 'Contenido Genérico CDMX', desc: 'Descripción...' }],
  otras: [{ id: 4, name: 'Contenido Genérico Otras', desc: 'Descripción...' }],
};

const standardButtons = [
  {icon: <FaPhone/>, text: 'Teléfonos'},
  {icon: <FaMapMarkerAlt/>, text: 'Google Maps'},
  {icon: <FaHeart/>, text: 'Favoritos'},
  {icon: <FaGlobe/>, text: 'Sitio Web'},
  {icon: <FaShareAlt/>, text: 'Share'}
];

// Datos específicos para la categoría DESTINOS
const destinationsData = {
  monterrey: [
    { id: 101, name: 'Parque Fundidora', image: '/images/placeholder.webp', rating: 5, price: 'Gratuito', status: 'Abierto', buttons: standardButtons },
    { id: 102, name: 'Cerro de La Silla', image: '/images/placeholder.webp', rating: 4, price: '$', status: 'Abierto', buttons: standardButtons },
    { id: 103, name: 'Macroplaza', image: '/images/placeholder.webp', rating: 4, status: 'Abierto', buttons: standardButtons },
    { id: 104, name: 'Museo MARCO', image: '/images/placeholder.webp', rating: 5, price: '$-$$', status: 'Abierto', buttons: standardButtons },
    { id: 105, name: 'Barrio Antiguo', image: '/images/placeholder.webp', rating: 4, status: 'Abierto', buttons: standardButtons },
    { id: 106, name: 'Bioparque Estrella', image: '/images/placeholder.webp', rating: 4, price: '$$', status: 'Abierto', buttons: standardButtons },
    { id: 107, name: 'Catedral de Monterrey', image: '/images/placeholder.webp', rating: 4, status: 'Abierto', buttons: standardButtons },
    { id: 108, name: 'Paseo Santa Lucía', image: '/images/placeholder.webp', rating: 4, status: 'Abierto', buttons: standardButtons },
    { id: 109, name: 'Museo del Barrio', image: '/images/placeholder.webp', rating: 4, price: '$', status: 'Abierto', buttons: standardButtons },
    { id: 110, name: 'Mercado San Juan', image: '/images/placeholder.webp', rating: 4, status: 'Abierto', buttons: standardButtons },
  ],
  guadalajara: [
    { id: 201, name: 'KARNES GARIBALDI', image: '/images/placeholder.webp', rating: 4, price: '$', status: 'Abierto', buttons: standardButtons },
    { id: 202, name: 'Zoologico Guadalajara', image: '/images/placeholder.webp', rating: 5, price: '$$', buttons: standardButtons },
    { id: 203, name: 'CORONA CAPITAL SESSIONS 2025', image: '/images/placeholder.webp', date: 'Próximamente', countdown: '', buttons: standardButtons },
  ],
  cdmx: [
    { id: 301, name: 'PUJOL', image: '/images/placeholder.webp', rating: 4, price: '$', status: 'Abierto', buttons: standardButtons },
    { id: 302, name: 'Teotihuacan Piramide del sol', image: '/images/placeholder.webp', rating: 5, price: '$$', buttons: standardButtons },
    { id: 303, name: 'VIVE LATINO 2025', image: '/images/placeholder.webp', date: 'Finalizado', countdown: 'Finalizado', buttons: standardButtons },
  ],
  otras: [
    { id: 401, name: 'BENAZUZA COCINA TECNOEMOCIONAL CANCUN', image: '/images/placeholder.webp', rating: 4, price: '$', status: 'Abierto', buttons: standardButtons },
    { id: 402, name: 'PLAYA DELFINES CANCUN', image: '/images/placeholder.webp', rating: 5, price: 'Gratuito', buttons: standardButtons },
    { id: 403, name: 'TOMORROWLAND TULUM 2025', image: '/images/placeholder.webp', date: 'Finalizado', countdown: 'Finalizado', buttons: standardButtons },
  ]
};

const Rating = ({ count }) => (
  <div className="rating">
    {[...Array(5)].map((_, i) => (
      <span key={i} className={i < count ? 'filled' : ''}>★</span>
    ))}
  </div>
);

const CategoryLightbox = ({ isOpen, onClose, category }) => {
  const [activeTab, setActiveTab] = useState('monterrey');

  if (!isOpen) return null;

  const businessData = category === 'destinos' ? destinationsData : genericBusinessData;

  const renderBusinessCards = (city) => {
    const cityData = businessData[city] || [];
    return cityData.map(item => (
      <div key={item.id} className="business-card">
        <img src={item.image} alt={item.name} className="business-card-image" />
        <div className="business-card-content">
          <h4>{item.name}</h4>
          <div className="card-meta">
            {item.rating && <Rating count={item.rating} />}
            {item.price && <span className="price">{item.price}</span>}
            {item.status && <span className="status">{item.status}</span>}
            {item.date && <span className="date">{item.date}</span>}
          </div>
          <div className="card-buttons">
            {item.buttons && item.buttons.map(button => (
              <button key={button.text}>{button.icon} {button.text}</button>
            ))}
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className="category-lightbox-overlay" onClick={onClose}>
      <div className="category-lightbox-container" onClick={(e) => e.stopPropagation()}>
        <div className="category-lightbox-header">
          <h2>{category}</h2>
          <button onClick={onClose} className="close-button">
            <FaTimes />
          </button>
        </div>
        <div className="category-lightbox-tabs">
          <button className={activeTab === 'monterrey' ? 'active' : ''} onClick={() => setActiveTab('monterrey')}>Monterrey</button>
          <button className={activeTab === 'guadalajara' ? 'active' : ''} onClick={() => setActiveTab('guadalajara')}>Guadalajara</button>
          <button className={activeTab === 'cdmx' ? 'active' : ''} onClick={() => setActiveTab('cdmx')}>CDMX</button>
          <button className={activeTab === 'otras' ? 'active' : ''} onClick={() => setActiveTab('otras')}>Otras Regiones</button>
        </div>
        <div className="category-lightbox-content">
          {activeTab && businessData[activeTab] ? renderBusinessCards(activeTab) : <p>Contenido para {activeTab} no disponible.</p>}
        </div>
        <div className="category-lightbox-footer">
            <button>Explorar más</button>
            <button>Ver Directorio Completo</button>
            <button>Otros destinos</button>
        </div>
      </div>
    </div>
  );
};

export default CategoryLightbox;
