import React, { useState } from 'react';
import { FaTimes, FaMapMarkerAlt, FaShareAlt, FaHeart, FaPhone, FaGlobe, FaWhatsapp } from 'react-icons/fa';
import './../styles/CategoryLightbox.css';

// --- DATOS ---

const genericBusinessData = {
    monterrey: [{ id: 1, name: 'Contenido Genérico MTY', desc: 'Descripción...' }],
    guadalajara: [{ id: 2, name: 'Contenido Genérico GDL', desc: 'Descripción...' }],
    cdmx: [{ id: 3, name: 'Contenido Genérico CDMX', desc: 'Descripción...' }],
    cancun: [{ id: 4, name: 'Contenido Genérico Cancun', desc: 'Descripción...' }],
    palenque: [{ id: 5, name: 'Contenido Genérico Palenque', desc: 'Descripción...' }],
    puertoVallarta: [{ id: 6, name: 'Contenido Genérico PV', desc: 'Descripción...' }],
    veracruz: [{ id: 7, name: 'Contenido Genérico Veracruz', desc: 'Descripción...' }],
    otras: [{ id: 8, name: 'Contenido Genérico Otras', desc: 'Descripción...' }],
};

const standardButtons = [
  {icon: <FaPhone/>, text: 'Teléfonos'},
  {icon: <FaMapMarkerAlt/>, text: 'Google Maps'},
  {icon: <FaHeart/>, text: 'Favoritos'},
  {icon: <FaGlobe/>, text: 'Sitio Web'},
  {icon: <FaShareAlt/>, text: 'Share'}
];

const destinationsData = {
  monterrey: {
    description: {
      image: '/images/monterrey.webp',
      historia: 'Nació en 1596, se convirtió en la capital industrial de México.',
      cultura: 'Fuerte valor al trabajo y la familia. Destacan la música y el baile norteño.',
      comida: ['Carne Asada', 'Tacos de Trompo', 'Cabrito', 'Machacado con huevo', 'Glorias'],
      transporte: ['Metro y TransMetro', 'Apps: Urbani (para recarga), Uber, Didi.'],
      seguridad: ['Zonas seguras: Centro, San Pedro, zona sur.', 'Tráfico pesado en horas pico.', 'Clima: Veranos muy calurosos.']
    },
    places: [
      { id: 101, name: 'Parque Fundidora',
        image: 'https://images.unsplash.com/photo-1591885600463-281c5b737375?q=80&w=400&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
        rating: 5,
        price: 'Gratuito',
        status: 'Abierto',
        description: 'Ubicado en el corazón de Monterrey, Nuevo León, el Parque Fundidora es un vasto espacio urbano que fusiona la historia industrial con la modernidad. Antiguamente una fundidora de acero, hoy es un parque público que alberga museos, centros de convenciones, una pista de hielo, lago artificial, áreas verdes y atracciones para toda la familia. Es un punto de encuentro para eventos culturales, deportivos y de entretenimiento, así como un pulmón verde para la ciudad.',
        schedules: 'Abierto todos los días de 6:00 AM a 10:00 PM. Los horarios de las atracciones y museos dentro del parque pueden variar.',
        phone: '52 81 8126 8500',
        direccion: 'Adolfo Prieto S/N, Obrera, 64010 Monterrey, N.L.',
        maps: 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d14383.130616553346!2d-100.30592199379967!3d25.67849862708608!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x86629561ff265483%3A0x1b7bd40783c3a9a8!2sParque%20Fundidora!5e0!3m2!1ses!2smx!4v1751316262950!5m2!1ses!2smx',
        web: 'https://www.parquefundidora.org/',
        buttons: standardButtons
      },
      { id: 102, name: 'Cerro de La Silla',
        image: 'https://images.unsplash.com/photo-1561158250-01426799e9e0?q=80&w=600&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
        rating: 4,
        price: '$',
        status: 'Abierto',
        description: 'El Cerro de la Silla es el monumento natural más emblemático de Monterrey y símbolo del estado de Nuevo León. Su silueta, que recuerda a una silla de montar, domina el paisaje de la ciudad. Es ideal para actividades de senderismo, observación de aves y fotografía de paisajes. Existen rutas señalizadas y miradores, siendo el Pico Norte y el antiguo teleférico los puntos más visitados. Altitud: 1,820 m. Dificultad: Media a alta. Recomendado para personas con buena condición física.',
        schedules: 'Abierto todos los días del año. Excursiones guiadas: Lunes a domingo, 6:00, 7:00, 16:00 y 17:00 hrs. Duración aproximada del recorrido: 4 horas.',
        phone: '+52 81 2584 8406',
        direccion: 'Juárez Esq. Corregidora, Centro, Guadalupe, Nuevo León. Acceso principal a 100 metros de la zona metropolitana de Monterrey.',
        whats: '+52 812 584 8406',
        maps: 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d57554.6402436923!2d-100.30849023737184!3d25.632648242637693!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x8662c08c31400855%3A0x43327b80ef78b9be!2sCerro%20de%20la%20Silla!5e0!3m2!1ses!2smx!4v1751318614973!5m2!1ses!2smx',
        buttons: standardButtons
      },
      { id: 103, name: 'Macroplaza', image: 'https://lh3.googleusercontent.com/gps-cs-s/AC9h4npZcyq-bkpjqT_uvQMzb7iAb2-Uw3LaJCpCQfkcz_yoFduZ6x26FEVasrwq2WuF-dHWHbK9lyB2S4ffDeEzE33pGMiaclNvPJbZxFvQjb4YcCKuE9DONwLAEEw7Y_q-echhfANw=s680-w680-h510-rw', rating: 4, status: 'Abierto', buttons: standardButtons },
      { id: 104, name: 'Museo MARCO', image: 'https://lh3.googleusercontent.com/p/AF1QipMv2vc3XKB2HZIqBLK-_xP3u6OKir9GChaTwKaz=s680-w680-h510-rw', rating: 5, price: '$-$$', status: 'Abierto', buttons: standardButtons },
      { id: 105, name: 'Barrio Antiguo', image: 'https://modaenlaciudad.com/wp-content/uploads/2023/02/Barrio-Antiguo-Monterrey-Moda-en-la-Ciudad-1.jpg', rating: 4, status: 'Abierto', buttons: standardButtons },
      { id: 106, name: 'Bioparque Estrella', image: 'https://bioparquemonterrey.mx/wp-content/uploads/2023/11/SOMOS2-845x684.jpg', rating: 4, price: '$$', status: 'Abierto', buttons: standardButtons },
      { id: 107, name: 'Catedral de Monterrey', image: 'https://lh3.googleusercontent.com/gps-cs-s/AC9h4np5qBJ4pwQOVJ6UIsVtOehW0dkk9ITPCtJvhfDIqx1Zdbv_J2i0As_hN_I5RgFWbp9LMCe1-6CgOhHsnl2QJkfoqjKz8xqMjZveFP9AcuEZ0Wc1GeDeuli7dlfyT86vtUrCLbmXqQ=s680-w680-h510-rw', rating: 4, status: 'Abierto', buttons: standardButtons },
      { id: 108, name: 'Paseo Santa Lucía', image: 'https://images.unsplash.com/photo-1618936656344-63c77e766a87?q=80&w=870&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', rating: 4, status: 'Abierto', buttons: standardButtons },
