import React, { useState } from 'react';
import { FaMapMarkerAlt, FaGlobe, FaPhone, FaWhatsapp, FaShare, FaTimes } from 'react-icons/fa';
import './../styles/GastronomiaLightbox.css';

const GastronomiaLightbox = ({ isOpen, onClose, selectedCategory, selectedSubcategory }) => {
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [showMap, setShowMap] = useState(false);

  const gastronomiaData = {
    monterrey: {
      name: 'Monterrey',
      description: 'Descubre la rica gastronomía de Monterrey, desde los tradicionales cabritos y carnes asadas hasta modernos cafés y restaurantes internacionales.',
      places: [
        { id: 801, name: 'El Rey del Cabrito', rating: 5, price: 'Precios desde $200', status: 'Abierto', description: 'Restaurante tradicional especializado en cabrito al pastor, platillo emblemático de Nuevo León. Ambiente familiar y servicio cálido.', schedules: 'Lun-Dom 12:00-22:00', phone: '81 8345 1234', direccion: 'Av. Constitución 1234, Centro, 64000 Monterrey, N.L.', whats: '8183451234', maps: 'NA', web: 'https://www.elreydelcabrito.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Cabrito al pastor, Carne asada, Queso fundido', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 802, name: 'Café Iguana', rating: 5, price: 'Precios desde $80', status: 'Abierto', description: 'Café de especialidad con ambiente bohemio, tostado artesanal y pastelería casera. Ideal para trabajar o reunirse.', schedules: 'Lun-Dom 7:00-23:00', phone: '81 8345 5678', direccion: 'Calle Morelos 567, Barrio Antiguo, 64000 Monterrey, N.L.', whats: '8183455678', maps: 'NA', web: 'https://www.cafeiguana.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Café de especialidad, Pastelería, Té artesanal', servicio: 'Comer en lugar, Para llevar' },
        { id: 803, name: 'La Nacional', rating: 5, price: 'Precios desde $150', status: 'Abierto', description: 'Restaurante histórico fundado en 1920, famoso por su carne asada y ambiente tradicional regiomontano.', schedules: 'Lun-Dom 12:00-23:00', phone: '81 8345 9012', direccion: 'Av. Madero 789, Centro, 64000 Monterrey, N.L.', whats: '8183459012', maps: 'NA', web: 'https://www.lanacional.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Carne asada, Machacado con huevo, Chicharrón', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 804, name: 'Vegan Planet', rating: 5, price: 'Precios desde $120', status: 'Abierto', description: 'Restaurante 100% vegano con opciones saludables y deliciosas. Menú variado con influencias internacionales.', schedules: 'Lun-Dom 11:00-22:00', phone: '81 8345 3456', direccion: 'Av. San Pedro 456, San Pedro Garza García, 66220 N.L.', whats: '8183453456', maps: 'NA', web: 'https://www.veganplanet.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Comida vegana, Jugos naturales, Postres sin azúcar', servicio: 'Comer en lugar, Servicio a domicilio, Para llevar' },
        { id: 805, name: 'Barrio Antiguo Brewing Co.', rating: 5, price: 'Precios desde $100', status: 'Abierto', description: 'Cervecería artesanal con gastropub, cervezas de la casa y comida fusión. Ambiente moderno y música en vivo.', schedules: 'Lun-Dom 12:00-02:00', phone: '81 8345 7890', direccion: 'Calle Abasolo 234, Barrio Antiguo, 64000 Monterrey, N.L.', whats: '8183457890', maps: 'NA', web: 'https://www.barrioantiguobrewing.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Cerveza artesanal, Hamburguesas gourmet, Wings', servicio: 'Comer en lugar, Para llevar' },
        { id: 806, name: 'El Gran Pastor', rating: 5, price: 'Precios desde $80', status: 'Abierto', description: 'Taquería tradicional especializada en pastor, con tortillas hechas a mano y salsas caseras.', schedules: 'Lun-Dom 11:00-03:00', phone: '81 8345 1235', direccion: 'Av. Universidad 567, 64460 Monterrey, N.L.', whats: '8183451235', maps: 'NA', web: 'https://www.elgranpastor.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Tacos al pastor, Quesadillas, Tortas', servicio: 'Comer en lugar, Servicio a domicilio, Para llevar' },
        { id: 807, name: 'Café Conquistador', rating: 5, price: 'Precios desde $70', status: 'Abierto', description: 'Café de tercera ola con tostado propio, ambiente acogedor y pastelería francesa.', schedules: 'Lun-Dom 7:00-22:00', phone: '81 8345 5679', direccion: 'Av. Revolución 890, 64000 Monterrey, N.L.', whats: '8183455679', maps: 'NA', web: 'https://www.cafeconquistador.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Café de especialidad, Croissants, Té gourmet', servicio: 'Comer en lugar, Para llevar' },
        { id: 808, name: 'Sushi Itto', rating: 5, price: 'Precios desde $180', status: 'Abierto', description: 'Restaurante japonés con sushi fresco, ramen y platillos tradicionales. Ambiente elegante y servicio impecable.', schedules: 'Lun-Dom 12:00-23:00', phone: '81 8345 9013', direccion: 'Av. Lázaro Cárdenas 1234, Valle Oriente, 66220 N.L.', whats: '8183459013', maps: 'NA', web: 'https://www.sushiitto.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Sushi, Ramen, Tempura, Sashimi', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 809, name: 'La Casa del Pastor', rating: 5, price: 'Precios desde $90', status: 'Abierto', description: 'Taquería familiar con más de 30 años de tradición, pastor jugoso y tortillas recién hechas.', schedules: 'Lun-Dom 10:00-02:00', phone: '81 8345 3457', direccion: 'Av. Constitución 2345, 64000 Monterrey, N.L.', whats: '8183453457', maps: 'NA', web: 'https://www.lacasadepastor.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Tacos al pastor, Gringas, Quesadillas', servicio: 'Comer en lugar, Servicio a domicilio, Para llevar' },
        { id: 810, name: 'Pangea', rating: 5, price: 'Precios desde $250', status: 'Abierto', description: 'Restaurante de alta cocina con menú degustación, fusión de sabores internacionales y presentación artística.', schedules: 'Mar-Sáb 19:00-23:00', phone: '81 8345 7891', direccion: 'Av. San Pedro 789, San Pedro Garza García, 66220 N.L.', whats: '8183457891', maps: 'NA', web: 'https://www.pangea.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Cocina de autor, Menú degustación, Vinos', servicio: 'Comer en lugar' },
        { id: 811, name: 'Café Central', rating: 5, price: 'Precios desde $60', status: 'Abierto', description: 'Café histórico en el centro de la ciudad, ambiente tradicional y café de olla auténtico.', schedules: 'Lun-Dom 6:00-21:00', phone: '81 8345 1236', direccion: 'Plaza Zaragoza 123, Centro, 64000 Monterrey, N.L.', whats: '8183451236', maps: 'NA', web: 'https://www.cafecentral.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Café de olla, Pan dulce, Chocolate caliente', servicio: 'Comer en lugar, Para llevar' },
        { id: 812, name: 'El Tío', rating: 5, price: 'Precios desde $120', status: 'Abierto', description: 'Restaurante de mariscos frescos, especializado en pescados y mariscos del Golfo de México.', schedules: 'Lun-Dom 12:00-22:00', phone: '81 8345 5680', direccion: 'Av. Revolución 3456, 64000 Monterrey, N.L.', whats: '8183455680', maps: 'NA', web: 'https://www.eltio.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Mariscos, Pescados, Ceviches, Cocteles', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 813, name: 'Veggie House', rating: 5, price: 'Precios desde $100', status: 'Abierto', description: 'Restaurante vegetariano con opciones veganas, comida saludable y deliciosa sin sacrificar el sabor.', schedules: 'Lun-Dom 11:00-21:00', phone: '81 8345 9014', direccion: 'Av. Universidad 890, 64460 Monterrey, N.L.', whats: '8183459014', maps: 'NA', web: 'https://www.veggiehouse.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Comida vegetariana, Jugos detox, Ensaladas', servicio: 'Comer en lugar, Servicio a domicilio, Para llevar' },
        { id: 814, name: 'La Divina', rating: 5, price: 'Precios desde $180', status: 'Abierto', description: 'Restaurante italiano auténtico con pasta fresca, pizzas de horno de piedra y ambiente romántico.', schedules: 'Lun-Dom 13:00-23:00', phone: '81 8345 3458', direccion: 'Av. Lázaro Cárdenas 5678, Valle Oriente, 66220 N.L.', whats: '8183453458', maps: 'NA', web: 'https://www.ladivina.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Pasta fresca, Pizza, Risotto, Vinos italianos', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 815, name: 'Café del Centro', rating: 5, price: 'Precios desde $65', status: 'Abierto', description: 'Café tradicional en el corazón del centro histórico, ideal para desayunos y meriendas.', schedules: 'Lun-Dom 7:00-20:00', phone: '81 8345 7892', direccion: 'Calle Morelos 456, Centro, 64000 Monterrey, N.L.', whats: '8183457892', maps: 'NA', web: 'https://www.cafedelcentro.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Café tradicional, Desayunos, Pan de dulce', servicio: 'Comer en lugar, Para llevar' },
        { id: 816, name: 'El Regio', rating: 5, price: 'Precios desde $110', status: 'Abierto', description: 'Restaurante de comida regional, especializado en cabrito, carne asada y platillos típicos de Nuevo León.', schedules: 'Lun-Dom 12:00-22:00', phone: '81 8345 1237', direccion: 'Av. Constitución 3456, 64000 Monterrey, N.L.', whats: '8183451237', maps: 'NA', web: 'https://www.elregio.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Cabrito, Carne asada, Machacado, Chicharrón', servicio: 'Comer en lugar, Servicio a domicilio' },
        { id: 817, name: 'Brew House', rating: 5, price: 'Precios desde $120', status: 'Abierto', description: 'Cervecería artesanal con más de 20 cervezas de la casa, comida pub y ambiente deportivo.', schedules: 'Lun-Dom 12:00-02:00', phone: '81 8345 5681', direccion: 'Av. Revolución 4567, 64000 Monterrey, N.L.', whats: '8183455681', maps: 'NA', web: 'https://www.brewhouse.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Cerveza artesanal, Hamburguesas, Wings, Nachos', servicio: 'Comer en lugar, Para llevar' },
        { id: 818, name: 'Dulces Tradicionales', rating: 5, price: 'Precios desde $40', status: 'Abierto', description: 'Dulcería tradicional con dulces típicos de Nuevo León, glorias, nueces garapiñadas y conservas caseras.', schedules: 'Lun-Dom 9:00-19:00', phone: '81 8345 9015', direccion: 'Calle Abasolo 789, Barrio Antiguo, 64000 Monterrey, N.L.', whats: '8183459015', maps: 'NA', web: 'https://www.dulcestradicionales.com/', buttons: [ {icon: 'map', text: 'Ver en Google Maps'}, {icon: 'web', text: 'Sitio web'} ], menu: 'Glorias, Nueces garapiñadas, Conservas, Dulces típicos', servicio: 'Comer en lugar, Para llevar' }
      ]
    }
  };

  const handlePlaceClick = (place) => {
    setSelectedPlace(place);
  };

  const handleCloseModal = () => {
    setSelectedPlace(null);
    setShowMap(false);
  };

  const handleButtonClick = (button, place) => {
    switch (button.icon) {
      case 'phone':
        if (place.phone && place.phone !== 'NA') {
          window.open(`tel:${place.phone}`);
        }
        break;
      case 'whatsapp':
        if (place.whats && place.whats !== 'NA') {
          const message = `Hola, me interesa ${place.name}`;
          window.open(`https://wa.me/${place.whats}?text=${encodeURIComponent(message)}`);
        }
        break;
      case 'map':
        if (place.maps && place.maps !== 'NA') {
          if (place.maps.includes('embed')) {
            setShowMap(true);
          } else {
            window.open(place.maps, '_blank');
          }
        }
        break;
      case 'web':
        if (place.web && place.web !== 'NA') {
          window.open(place.web, '_blank');
        }
        break;
      case 'share':
        if (navigator.share) {
          navigator.share({
            title: place.name,
            text: place.description,
            url: place.web || window.location.href
          });
        } else {
          navigator.clipboard.writeText(`${place.name} - ${place.web || window.location.href}`);
          alert('Enlace copiado al portapapeles');
        }
        break;
      default:
        break;
    }
  };

  const getCurrentData = () => {
    return gastronomiaData[selectedSubcategory] || null;
  };

  const currentData = getCurrentData();

  if (!isOpen || !currentData) return null;

  return (
    <div className="gastronomia-lightbox-overlay" onClick={onClose}>
      <div className="gastronomia-lightbox-container" onClick={(e) => e.stopPropagation()}>
        <div className="gastronomia-lightbox-header">
          <div>
            <h2>{currentData.name}</h2>
            <p>{currentData.description}</p>
          </div>
          <button
            onClick={onClose}
            className="gastronomia-modal-close"
          >
            <FaTimes />
          </button>
        </div>

        <div className="gastronomia-lightbox-content">
          <div className="gastronomia-grid">
            {currentData.places.map((place) => (
              <div
                key={place.id}
                className="gastronomia-card"
                onClick={() => handlePlaceClick(place)}
              >
                <div className="gastronomia-card-header">
                  <h3 className="gastronomia-card-title">{place.name}</h3>
                  <div className="gastronomia-card-rating">
                    <span>★</span>
                    <span>{place.rating}</span>
                  </div>
                </div>
                
                <div className="gastronomia-card-meta">
                  <p className="gastronomia-card-price">{place.price}</p>
                  <span className={`gastronomia-card-status ${
                    place.status === 'Abierto' ? 'open' : 'closed'
                  }`}>
                    {place.status}
                  </span>
                </div>

                <p className="gastronomia-card-description">{place.description}</p>
                
                <div className="gastronomia-card-details">
                  <p><strong>Horarios:</strong> {place.schedules}</p>
                  <p><strong>Dirección:</strong> {place.direccion}</p>
                  {place.menu && (
                    <p><strong>Menú:</strong> {place.menu}</p>
                  )}
                  {place.servicio && (
                    <p><strong>Servicio:</strong> {place.servicio}</p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal para detalles del lugar */}
      {selectedPlace && (
        <div className="gastronomia-modal-overlay" onClick={handleCloseModal}>
          <div className="gastronomia-modal-container" onClick={(e) => e.stopPropagation()}>
            <div className="gastronomia-modal-header">
              <div>
                <h3 className="gastronomia-modal-title">{selectedPlace.name}</h3>
                <div className="gastronomia-modal-rating">
                  <span>★</span>
                  <span>{selectedPlace.rating}</span>
                </div>
              </div>
              <button
                onClick={handleCloseModal}
                className="gastronomia-modal-close"
              >
                <FaTimes />
              </button>
            </div>

            <div className="gastronomia-modal-content">
              <div className="gastronomia-modal-grid">
                <div className="gastronomia-modal-info">
                  <div className="gastronomia-modal-section">
                    <h4>Información</h4>
                    <p className="gastronomia-card-price">{selectedPlace.price}</p>
                    <span className={`gastronomia-card-status ${
                      selectedPlace.status === 'Abierto' ? 'open' : 'closed'
                    }`}>
                      {selectedPlace.status}
                    </span>
                  </div>

                  <div className="gastronomia-modal-section">
                    <h4>Descripción</h4>
                    <p>{selectedPlace.description}</p>
                  </div>

                  <div className="gastronomia-modal-section">
                    <h4>Detalles</h4>
                    <div className="gastronomia-card-details">
                      <p><strong>Horarios:</strong> {selectedPlace.schedules}</p>
                      <p><strong>Dirección:</strong> {selectedPlace.direccion}</p>
                      {selectedPlace.phone && selectedPlace.phone !== 'NA' && (
                        <p><strong>Teléfono:</strong> {selectedPlace.phone}</p>
                      )}
                      {selectedPlace.menu && (
                        <p><strong>Menú:</strong> {selectedPlace.menu}</p>
                      )}
                      {selectedPlace.servicio && (
                        <p><strong>Servicio:</strong> {selectedPlace.servicio}</p>
                      )}
                    </div>
                  </div>

                  <div className="gastronomia-modal-buttons">
                    {selectedPlace.buttons.map((button, index) => (
                      <button
                        key={index}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleButtonClick(button, selectedPlace);
                        }}
                        className="gastronomia-modal-button secondary"
                      >
                        {button.icon === 'phone' && <FaPhone />}
                        {button.icon === 'whatsapp' && <FaWhatsapp />}
                        {button.icon === 'map' && <FaMapMarkerAlt />}
                        {button.icon === 'web' && <FaGlobe />}
                        {button.icon === 'share' && <FaShare />}
                        {button.text}
                      </button>
                    ))}
                    {selectedPlace.whats && selectedPlace.whats !== 'NA' && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleButtonClick({icon: 'whatsapp'}, selectedPlace);
                        }}
                        className="gastronomia-modal-button whatsapp"
                      >
                        <FaWhatsapp />
                        WhatsApp
                      </button>
                    )}
                  </div>
                </div>

                <div>
                  {showMap && selectedPlace.maps && selectedPlace.maps !== 'NA' && (
                    <div className="gastronomia-modal-map">
                      <h4>Ubicación</h4>
                      <div className="relative">
                        <iframe
                          src={selectedPlace.maps}
                          width="100%"
                          height="300"
                          style={{ border: 0 }}
                          allowFullScreen=""
                          loading="lazy"
                          referrerPolicy="no-referrer-when-downgrade"
                        ></iframe>
                        <button
                          onClick={() => setShowMap(false)}
                          className="gastronomia-modal-close"
                          style={{ position: 'absolute', top: '10px', right: '10px' }}
                        >
                          <FaTimes />
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GastronomiaLightbox;
