import React from 'react';
import { FaTimes } from 'react-icons/fa';
import './../styles/CategoryLightbox.css';
import CarteleraTabs from './CarteleraTabs';
import DestinosTabs from './DestinosTabs';
import GastronomiaTabs from './GastronomiaTabs';
import HospedajeTabs from './HospedajeTabs';
import ServiciosTabs from './ServiciosTabs';
import EventosTabs from './EventosTabs';
import PromocionesTabs from './PromocionesTabs';
import DirectorioTabs from './DirectorioTabs';

const CategoryLightbox = ({ isOpen, onClose, category }) => {
  if (!isOpen) return null;

  const renderContent = () => {
    switch (category) {
      case 'cartelera':
        return <CarteleraTabs />;
      case 'destinos':
        return <DestinosTabs />;
      case 'gastronomia':
        return <GastronomiaTabs />;
      case 'hospedaje':
        return <HospedajeTabs />;
      case 'servicios':
        return <ServiciosTabs />;
      case 'eventos':
        return <EventosTabs />;
      case 'promociones':
        return <PromocionesTabs />;
      case 'directorio':
        return <DirectorioTabs />;
      default:
        return (
          <div style={{ padding: '2rem', textAlign: 'center' }}>
            Contenido no disponible
          </div>
        );
    }
  };

  return (
    <div className="category-lightbox-overlay" onClick={onClose}>
      <div className="category-lightbox-container" onClick={(e) => e.stopPropagation()}>
        <div className="category-lightbox-header">
          <h2>{category}</h2>
          <button onClick={onClose} className="close-button">
            <FaTimes />
          </button>
        </div>
        {renderContent()}
      </div>
    </div>
  );
};

export default CategoryLightbox;
