import React from 'react';
import Swiper, { Navigation, Pagination, Autoplay } from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './HeroSlider.css';
import { useEffect } from 'react';

const HeroSlider = () => {
  useEffect(() => {
    const swiper = new Swiper('.swiper-container', {
      modules: [Navigation, Pagination, Autoplay],
      slidesPerView: 1,
      spaceBetween: 30,
      loop: true,
      autoplay: {
        delay: 2500,
        disableOnInteraction: false,
      },
      pagination: {
        el: '.swiper-pagination',
        clickable: true,
      },
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
    });
  }, []);

  return (
    <div className="hero-slider">
      <div className="swiper-container">
        <div className="swiper-wrapper">
          <div className="swiper-slide"><img src="public/images/monterrey.webp" alt="Monterrey" /></div>
          <div className="swiper-slide"><img src="public/images/guadalajara.webp" alt="Guadalajara" /></div>
          <div className="swiper-slide"><img src="public/images/cdmx.webp" alt="CDMX" /></div>
          <div className="swiper-slide"><img src="public/images/cancun.webp" alt="Cancun" /></div>
          <div className="swiper-slide"><img src="public/images/palenque.webp" alt="Palenque" /></div>
          <div className="swiper-slide"><img src="public/images/puerto-vallarta.webp" alt="Puerto Vallarta" /></div>
          <div className="swiper-slide"><img src="public/images/veracruz.webp" alt="Veracruz" /></div>
        </div>
        <div className="swiper-pagination"></div>
        <div className="swiper-button-prev"></div>
        <div className="swiper-button-next"></div>
      </div>
    </div>
  );
};

export default HeroSlider;
