import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import { ThemeProvider, useTheme } from './utils/theme'; // Importa ThemeProvider y useTheme
import HeroSlider from './components/HeroSlider';
import MainContent from './components/MainContent';

function AppContent() {
  const { theme } = useTheme();

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div className="App">
      <Header />
      <HeroSlider />
      <SearchFilter />
      <MainContent />
      <main>
        <section className="hero-section">
          <h1>Bienvenido a GUIATREND</h1>
          <p>Tu guía completa para explorar México.</p>
        </section>
        {/* Otras secciones de la homepage */}
      </main>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
