import React, { useEffect } from 'react';
import Swiper from 'swiper';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import '../styles/HeroSlider.css';

const HeroSlider = () => {
  useEffect(() => {
    let swiper;
    const initializeSwiper = () => {
      swiper = new Swiper('.swiper-container', {
        modules: [Navigation, Pagination, Autoplay],
        slidesPerView: 1,
        spaceBetween: 30,
        loop: true,
        autoplay: {
          delay: 2500,
          disableOnInteraction: false,
        },
        pagination: {
          el: '.swiper-pagination',
          clickable: true,
        },
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        },
      });
    };

    initializeSwiper();

    return () => {
      if (swiper) {
        swiper.destroy();
      }
    };
  }, []);

  return (
    <div className="hero-slider">
      <div className="swiper-container">
        <div className="swiper-wrapper">
          <div className="swiper-slide"><img src="./images/monterrey.webp" alt="Monterrey" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/guadalajara.webp" alt="Guadalajara" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/cdmx.webp" alt="CDMX" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/cancun.webp" alt="Cancun" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/palenque.webp" alt="Palenque" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/puerto-vallarta.webp" alt="Puerto Vallarta" style={{ width: '100%', height: 'auto' }} /></div>
          <div className="swiper-slide"><img src="./images/veracruz.webp" alt="Veracruz" style={{ width: '100%', height: 'auto' }} /></div>
        </div>
        <div className="swiper-pagination"></div>
        <div className="swiper-button-prev"></div>
        <div className="swiper-button-next"></div>
      </div>
    </div>
  );
};

export default HeroSlider;
