import React from 'react';
import CarteleraTabs from './CarteleraTabs';
import DestinosTabs from './DestinosTabs';
import GastronomiaTabs from './GastronomiaTabs';
import HospedajeTabs from './HospedajeTabs';
import ServiciosTabs from './ServiciosTabs';
import EventosTabs from './EventosTabs';
import PromocionesTabs from './PromocionesTabs';
import DirectorioTabs from './DirectorioTabs';

const CategoryLightbox = ({ isOpen, onClose, category }) => {
  if (!isOpen) return null;

  if (category === 'cartelera') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <CarteleraTabs />
      </div>
    );
  }

  if (category === 'destinos') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <DestinosTabs />
      </div>
    );
  }

  if (category === 'gastronomia') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <GastronomiaTabs />
      </div>
    );
  }

  if (category === 'hospedaje') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <HospedajeTabs />
      </div>
    );
  }

  if (category === 'servicios') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <ServiciosTabs />
      </div>
    );
  }

  if (category === 'eventos') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <EventosTabs />
      </div>
    );
  }

  if (category === 'promociones') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <PromocionesTabs />
      </div>
    );
  }

  if (category === 'directorio') {
    return (
      <div className="category-lightbox">
        <button onClick={onClose}>Cerrar</button>
        <DirectorioTabs />
      </div>
    );
  }

  // Si la categoría no existe
  return (
    <div className="category-lightbox">
      <button onClick={onClose}>Cerrar</button>
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        Contenido no disponible
      </div>
    </div>
  );
};

export default CategoryLightbox;
