import React, { useState } from 'react';
import { FaSearch, FaMapMarkerAlt, FaTag, FaTimes, FaFilter } from 'react-icons/fa';
import './SearchBar3.css';

const SearchBar3 = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log('Búsqueda realizada:', searchQuery);
  };

  return (
    <div className="search-container">
      <div className="search-bar">
        <form onSubmit={handleFormSubmit} className="search-form">
          {/* Input principal siempre visible */}
          <div className="search-input-group">
            <FaSearch className="search-icon" />
            <input
              type="text"
              className="search-input"
              placeholder="Buscar en GUIATREND..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchQuery && (
              <button type="button" className="clear-button" onClick={() => setSearchQuery('')}>
                <FaTimes />
              </button>
            )}
          </div>

          {/* Filtros siempre visibles */}
          <div className="search-filters">
            <div className="filter-group">
              <FaMapMarkerAlt className="filter-icon" />
              <select>
                <option value="">Todas las Ciudades</option>
                <option value="monterrey">Monterrey</option>
                <option value="guadalajara">Guadalajara</option>
                <option value="cdmx">CDMX</option>
                <option value="cancun">Cancún</option>
                <option value="palenque">Palenque</option>
                <option value="puerto-vallarta">Puerto Vallarta</option>
                <option value="veracruz">Veracruz</option>
              </select>
            </div>
            <div className="filter-group">
              <FaTag className="filter-icon" />
              <select>
                <option value="">Todas las Categorías</option>
                <option value="restaurantes">Restaurantes</option>
                <option value="bares">Bares</option>
                <option value="turismo">Turismo</option>
                <option value="servicios">Servicios</option>
                <option value="eventos">Eventos</option>
                <option value="promociones">Promociones</option>
              </select>
            </div>
            <button type="submit" className="search-button">
              <FaFilter />
              <span>Aplicar</span>
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SearchBar3;
