import React, { useState } from 'react';
import { FaTimes, FaMapMarkerAlt, FaShareAlt, FaHeart, FaPlus } from 'react-icons/fa';
import './../styles/CategoryLightbox.css';

// Datos de ejemplo
const businessData = {
  monterrey: [
    { id: 1, name: 'El Rey del Cabrito', desc: 'Cena con mariachi en vivo' },
    { id: 2, name: 'Parque Fundidora', desc: 'Evento cultural' },
    { id: 3, name: 'Macroplaza', desc: 'Shopping nocturno' },
  ],
  guadalajara: [
    { id: 4, name: 'Karnes Garibaldi', desc: 'Las carnes en su jugo más rápidas del mundo' },
    { id: 5, name: 'Zoológico de Guadalajara', desc: 'Safari y vistas panorámicas' },
    { id: 6, name: 'Corona Capital Sessions', desc: 'Música en vivo' },
  ],
  cdmx: [
    { id: 7, name: 'Pujol', desc: 'Alta cocina mexicana' },
    { id: 8, name: 'Teotihuacán', desc: 'Pirámides milenarias' },
    { id: 9, name: 'Vive Latino', desc: 'Festival de rock' },
  ],
};

const CategoryLightbox = ({ isOpen, onClose, category }) => {
  const [activeTab, setActiveTab] = useState('monterrey');

  if (!isOpen) return null;

  const renderBusinessCards = (city) => {
    return businessData[city].map(item => (
      <div key={item.id} className="business-card">
        <h4>{item.name}</h4>
        <p>{item.desc}</p>
        <div className="card-buttons">
          <button><FaMapMarkerAlt /> Cómo llegar</button>
          <button><FaShareAlt /> Compartir</button>
          <button><FaHeart /> Favoritos</button>
          <button><FaPlus /> Explorar más</button>
        </div>
      </div>
    ));
  };

  return (
    <div className="category-lightbox-overlay">
      <div className="category-lightbox-container">
        <div className="category-lightbox-header">
          <h2>{category}</h2>
          <button onClick={onClose} className="close-button">
            <FaTimes />
          </button>
        </div>
        <div className="category-lightbox-tabs">
          <button className={activeTab === 'monterrey' ? 'active' : ''} onClick={() => setActiveTab('monterrey')}>Monterrey</button>
          <button className={activeTab === 'guadalajara' ? 'active' : ''} onClick={() => setActiveTab('guadalajara')}>Guadalajara</button>
          <button className={activeTab === 'cdmx' ? 'active' : ''} onClick={() => setActiveTab('cdmx')}>CDMX</button>
          <button className={activeTab === 'otras' ? 'active' : ''} onClick={() => setActiveTab('otras')}>Otras Regiones</button>
        </div>
        <div className="category-lightbox-content">
          {activeTab && businessData[activeTab] ? renderBusinessCards(activeTab) : <p>Contenido para {activeTab} no disponible.</p>}
        </div>
      </div>
    </div>
  );
};

export default CategoryLightbox;
