import React, { useState } from 'react';
import '../styles/SearchBar.css';
import CityFilter from './CityFilter';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const mockData = [
    { id: 1, name: 'Restaurant A', category: 'Food', city: 'Monterrey' },
    { id: 2, name: 'Hotel B', category: 'Accommodation', city: 'Guadalajara' },
    { id: 3, name: 'Shop C', category: 'Shopping', city: 'Cancun' },
  ];

  const [cityFilter, setCityFilter] = useState('');

  const handleSearch = (event) => {
    const term = event.target.value;
    setSearchTerm(term);

    let results = mockData;

    if (cityFilter) {
      results = results.filter(item => item.city === cityFilter);
    }

    results = results.filter(item =>
      item.name.toLowerCase().includes(term.toLowerCase())
    );
    setSearchResults(results);
  };

  const handleCityFilterChange = (event) => {
    setCityFilter(event.target.value);
    handleSearch({ target: { value: searchTerm } }); // Trigger search with the current term
  };

  return (
    <div className="search-bar">
      <input
        type="text"
        className="search-input"
        placeholder="Busca lo mejor de la GuÃ­a"
        value={searchTerm}
        onChange={handleSearch}
      />
      <CityFilter onChange={handleCityFilterChange} />
      <ul>
        {searchResults.map(result => (
          <li key={result.id}>{result.name} ({result.category}) - {result.city}</li>
        ))}
      </ul>
    </div>
  );
};

export default SearchBar;
