import React, { useState, useEffect, useRef } from 'react';
import './../styles/Header.css';
import { useTheme } from '../utils/theme'; // Importa useTheme

const Header = () => {
  const { theme, toggleTheme } = useTheme();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  
  // Dropdowns
  const [openDropdown, setOpenDropdown] = useState(null); // 'destinos' | 'directorio' | null
  const [dropdownPos, setDropdownPos] = useState({ left: 0, top: 0, width: 0 });
  const destinosBtnRef = useRef(null);
  const directorioBtnRef = useRef(null);

  // Persistencia para el estado de login
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('isLoggedIn') === 'true';
  });

  // Persistencia para el idioma
  const [language, setLanguage] = useState(() => {
    return localStorage.getItem('language') || 'es';
  });

  useEffect(() => {
    localStorage.setItem('isLoggedIn', isLoggedIn);
  }, [isLoggedIn]);

  useEffect(() => {
    localStorage.setItem('language', language);
    // Aqu√≠ se podr√≠a integrar i18next para cambiar el idioma globalmente
  }, [language]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleLogin = () => {
    setIsLoggedIn(prev => !prev);
  };

  const toggleLanguage = () => {
    setLanguage(prevLang => (prevLang === 'es' ? 'en' : 'es'));
  };

  // Manejo de dropdowns
  const handleDropdown = (type) => {
    if (openDropdown === type) {
      setOpenDropdown(null);
      return;
    }
    let btnRef = type === 'destinos' ? destinosBtnRef : directorioBtnRef;
    if (btnRef.current) {
      const rect = btnRef.current.getBoundingClientRect();
      setDropdownPos({
        left: rect.left + window.scrollX,
        top: rect.bottom + window.scrollY,
        width: rect.width
      });
    }
    setOpenDropdown(type);
  };

  // Cerrar dropdown al hacer click fuera
  useEffect(() => {
    const handleClick = (e) => {
      if (
        destinosBtnRef.current && destinosBtnRef.current.contains(e.target)
      ) return;
      if (
        directorioBtnRef.current && directorioBtnRef.current.contains(e.target)
      ) return;
      setOpenDropdown(null);
    };
    if (openDropdown) {
      document.addEventListener('mousedown', handleClick);
    } else {
      document.removeEventListener('mousedown', handleClick);
    }
    return () => document.removeEventListener('mousedown', handleClick);
  }, [openDropdown]);

  return (
    <>
      <header className={`header ${theme}`}>
      <div className="header-left">
        <div className="logo">
          <span className="logo-guia">GU√çA</span>
          <span className="logo-trend">TREND</span>
        </div>
      </div>

      <nav className={`main-nav ${isMenuOpen ? 'open' : ''}`}>
        <ul>
          <li><a href="#inicio" className="menu-item">Inicio</a></li>
          <li className="dropdown">
            <a
              href="#destinos"
              className="menu-item"
              ref={destinosBtnRef}
              onClick={e => { e.preventDefault(); handleDropdown('destinos'); }}
            >
              Destinos
            </a>
          </li>
          <li className="dropdown">
            <a
              href="#directorio"
              className="menu-item"
              ref={directorioBtnRef}
              onClick={e => { e.preventDefault(); handleDropdown('directorio'); }}
            >
              Directorio
            </a>
          </li>
          <li><a href="#eventos" className="menu-item">Eventos</a></li>
          <li><a href="#cartelera" className="menu-item">Cartelera</a></li>
          <li><a href="#blog" className="menu-item">Blog</a></li>
          <li><a href="#nosotros" className="menu-item">Nosotros</a></li>
          <li><a href="#contacto" className="menu-item">Contacto</a></li>
        </ul>
      </nav>

      <div className="header-right">
        {/* Switch de Modo Oscuro/Claro */}
        <div className="theme-switch-container">
          <button className="theme-switch" onClick={toggleTheme}>
            <span className="theme-switch-handle" style={{ left: theme === 'dark' ? 'calc(100% - 25px)' : '5px' }}></span>
          </button>
          <span className="theme-label">{theme === 'dark' ? 'üåô' : '‚òÄÔ∏è'}</span>
        </div>

        {/* Switch de Login/Logeado */}
        <div className="login-switch-container">
          <button className="login-switch" onClick={toggleLogin}>
            <span className="login-icon" style={{ backgroundColor: isLoggedIn ? '#FF004F' : '#CDCDCD' }}>üë§</span>
          </button>
          <span className="login-label">{isLoggedIn ? 'Online' : 'Offline'}</span>
        </div>

        {/* Switch de Idioma */}
        <div className="language-switch-container">
          <button className="language-switch" onClick={toggleLanguage}>
            {language === 'es' ? 'üá≤üáΩ ESP' : 'üá¨üáß ENG'}
          </button>
        </div>

        {/* Men√∫ Hamburguesa para m√≥viles */}
        <button className="hamburger-menu" onClick={toggleMenu}>
          ‚ò∞
        </button>
      </div>
      </header>

      {/* Dropdowns fuera del header */}
      {openDropdown === 'destinos' && (
        <div
          className="dropdown-content fixed-dropdown"
          style={{
            left: dropdownPos.left,
            top: dropdownPos.top,
            width: dropdownPos.width,
            position: 'fixed',
            zIndex: 9999
          }}
        >
          <a href="#monterrey">Monterrey</a>
          <a href="#guadalajara">Guadalajara</a>
          <a href="#cdmx">CDMX</a>
          <a href="#cancun">Canc√∫n</a>
          <a href="#palenque">Palenque</a>
          <a href="#puerto-vallarta">Puerto Vallarta</a>
          <a href="#veracruz">Veracruz</a>
        </div>
      )}
      {openDropdown === 'directorio' && (
        <div
          className="dropdown-content fixed-dropdown"
          style={{
            left: dropdownPos.left,
            top: dropdownPos.top,
            width: dropdownPos.width,
            position: 'fixed',
            zIndex: 9999
          }}
        >
          <a href="#restaurantes">Restaurantes</a>
          <a href="#bares">Bares</a>
          <a href="#turismo">Turismo</a>
          <a href="#servicios">Servicios</a>
          <a href="#eventos-dir">Eventos</a>
        </div>
      )}

      <a
        href="https://wa.me/525665828832"
        className="whatsapp-button"
        target="_blank"
        rel="noopener noreferrer"
      >
        <i className="fab fa-whatsapp"></i>
      </a>
    </>
  );
};

export default Header;
